<?php /** * ENGRAVE 客服中心：有问必答/投诉理赔 * =========================================================== * * 版权所有 2014-2019 青岛友腾信息科技有限公司，并保留所有权利。 * 网站地址: http://www.qdutsoft.com； * ---------------------------------------------------------- * 这不是一个自由软件！您只能在不用于商业目的的前提下对程序代码进行修改和 * 使用；不允许对程序代码以任何形式任何目的的再发布。 * ========================================================== * $Author: zhangxingpeng $ * $Id: lib_faq.php 17217 2014年11月28日 11时03分00秒 zhangxingpeng $ *//** * 添加 * @param unknown $faq：有问必答对象 * @return boolean：若添加成功，返回true，反之返回false！ */function engrave_faq_insert($faq){	//获取当前时间	$time=gmtime();	$sql="insert into ".$GLOBALS['engrave']->table('faq').	"(faq_title,faq_expressnumber,faq_orderno,faq_deliverynumber,faq_remark,".	"faq_userid,faq_parentid,faq_time,".	"faq_isdelete) values('".	$faq['faq_title']."','".$faq['faq_expressnumber']."','".$faq['faq_orderno']."','".$faq['faq_deliverynumber']."','".$faq['faq_remark']."','".	$faq['faq_userid']."','".$faq['faq_parentid']."','".$time.	"',0".	")";	$val = $GLOBALS['engrave_db']->query($sql);	if($val == 1)	{		return true;	}	else {		return false;	}}/** * 删除 * @param unknown $faq_id：FAQID * @return boolean：若删除成功，返回true，反之，返回false！ */function engrave_faq_delete($faq_id=0){	//获取当前时间	$time=gmtime();	$sql="update ".$GLOBALS['engrave']->table('faq').	" set faq_isdelete = 1 where faq_id = ".$faq_id;	$val = $GLOBALS['engrave_db']->query($sql);	if($val == 1)	{		return true;	}	else {		return false;	}}/** * 修改 * @param unknown $faq：有问必答对象 * @return boolean：若修改成功，返回true，反之返回false！ */function engrave_faq_update($faq){	$sql="update ".$GLOBALS['engrave']->table('faq').	"set faq_title='".$faq['faq_title'].	"',faq_expressnumber='".$faq['faq_expressnumber'].	"',faq_orderno='".$faq['faq_orderno'].	"',faq_deliverynumber='".$faq['faq_deliverynumber'].	"',faq_remark='".$faq['faq_remark'].	"' where faq_id = ".$faq['faq_id'];		$val = $GLOBALS['engrave_db']->query($sql);	if($val == 1)	{		return true;	}	else {		return false;	}}/** * 查询所有 * @param number $user_id：有问必答 用户ID * @return array:faq对象列表 */function engrave_faq_list($user_id=0){	$sql="select faq_id,faq_title,faq_expressnumber,faq_orderno,faq_deliverynumber,faq_remark,".	"faq_userid,faq_parentid,faq_time,reply_status from ".$GLOBALS['engrave']->table('faq').	" where faq_isdelete = 0 and faq_userid=".$user_id." order by faq_id desc";		$faq_list = $GLOBALS['engrave_db']->getAll($sql);	foreach ($faq_list as $key=>$val)	{		$faq_list[$key]['faq_time'] = local_date('Y-m-d',$val['faq_time']);	}	return $faq_list;}/** * 查询 * @param number $faq_pid：有问必答 父ID * @return array:faq对象列表 */function engrave_faq_plist($faq_pid){	$sql="select faq_id,faq_title,faq_expressnumber,faq_orderno,faq_deliverynumber,faq_remark,".	"faq_userid,faq_parentid,faq_time,user_name from ".$GLOBALS['engrave']->table('faq').	" left join ".$GLOBALS['engrave_shop']->table('admin_user').	" on faq_userid = user_id".	" where faq_isdelete = 0 and faq_parentid=".$faq_pid." order by faq_id desc";		$faq_list = $GLOBALS['engrave_db']->getAll($sql);	foreach ($faq_list as $key=>$val)	{		$faq_list[$key]['faq_time'] = local_date('Y-m-d H:i:s',$val['faq_time']);	}	return $faq_list;}/** * 查询 * @param number $faq_id：有问必答 ID * @param number $user_id：有问必答 用户ID */function engrave_faq($faq_id=0,$user_id=0){	$sql="select faq_id,faq_title,faq_expressnumber,faq_orderno,faq_deliverynumber,faq_remark,".	"faq_userid,faq_parentid,faq_time from ".$GLOBALS['engrave']->table('faq').	" where faq_isdelete = 0 and faq_id =".$faq_id." and faq_userid=".$user_id;	$faq = $GLOBALS['engrave_db']->getRow($sql);	$faq['faq_time'] = local_date('Y-m-d H:i:s',$faq['faq_time']);	return $faq;}/** * 添加 * @param unknown $complaint：投诉理赔对象 * @return boolean：若添加成功，返回true，反之返回false！ */function engrave_complaint_insert($cmp){	//获取当前时间	$time=gmtime();	$sql="insert into ".$GLOBALS['engrave']->table('complaint').	"(cmp_title,cmp_expressnumber,cmp_orderno,cmp_deliverynumber,cmp_remark,".	"cmp_userid,cmp_parentid,cmp_time,".	"cmp_isdelete) values('".	$cmp['cmp_title']."','".$cmp['cmp_expressnumber']."','".$cmp['cmp_orderno']."','".$cmp['cmp_deliverynumber']."','".$cmp['cmp_remark']."','".	$cmp['cmp_userid']."','".$cmp['cmp_parentid']."','".$time.	"',0".	")";	$val = $GLOBALS['engrave_db']->query($sql);	if($val == 1)	{		return true;	}	else {		return false;	}}/** * 删除 * @param unknown $complaint_id：投诉理赔ID * @return boolean：若删除成功，返回true，反之，返回false！ */function engrave_complaint_delete($cmp_id=0){	//获取当前时间	$time=gmtime();	$sql="update ".$GLOBALS['engrave']->table('complaint').	" set cmp_isdelete = 1 where cmp_id = ".$cmp_id;	$val = $GLOBALS['engrave_db']->query($sql);	if($val == 1)	{		return true;	}	else {		return false;	}}/** * 修改 * @param unknown $complaint：投诉理赔对象 * @return boolean：若修改成功，返回true，反之返回false！ */function engrave_complaint_update($cmp){	$sql="update ".$GLOBALS['engrave']->table('complaint').	"set cmp_title='".$cmp['cmp_title'].	"',cmp_expressnumber='".$cmp['cmp_expressnumber'].	"',cmp_orderno='".$cmp['cmp_orderno'].	"',cmp_deliverynumber='".$cmp['cmp_deliverynumber'].	"',cmp_remark='".$cmp['cmp_remark'].	"' where cmp_id = ".$cmp['cmp_id'];	$val = $GLOBALS['engrave_db']->query($sql);	if($val == 1)	{		return true;	}	else {		return false;	}}/** * 查询所有 * @param number $user_id：投诉理赔  用户ID */function engrave_complaint_list($user_id=0){	$sql="select cmp_id,cmp_title,cmp_expressnumber,cmp_orderno,cmp_deliverynumber,cmp_remark,".			"cmp_userid,cmp_parentid,cmp_time from ".$GLOBALS['engrave']->table('complaint').			" where cmp_isdelete = 0 and cmp_userid=".$user_id." order by cmp_id desc";	$complaint_list = $GLOBALS['engrave_db']->getAll($sql);	foreach ($complaint_list as $key=>$val)	{		$complaint_list[$key]['cmp_time'] = local_date('Y-m-d',$val['cmp_time']);	}	return $complaint_list;}/** * 查询parentid * @param number $user_id：投诉理赔 父ID */function engrave_complaint_plist($cmp_parentid=0){	$sql="select cmp_id,cmp_title,cmp_expressnumber,cmp_orderno,cmp_deliverynumber,cmp_remark,".	"cmp_userid,cmp_parentid,cmp_time,user_name from ".$GLOBALS['engrave']->table('complaint').	" left join ".$GLOBALS['engrave_shop']->table('users').	" on cmp_userid = user_id ".	" where cmp_isdelete = 0 and cmp_parentid=".$cmp_parentid.	" order by cmp_id desc";	$complaint_list = $GLOBALS['engrave_db']->getAll($sql);	foreach ($complaint_list as $key=>$val)	{		$complaint_list[$key]['cmp_time'] = local_date('Y-m-d H:i:s',$val['cmp_time']);	}	return $complaint_list;}/** * 查询 * @param unknown $cmp_id：投诉理赔 父ID * @param number $user_id：投诉理赔  用户ID */function engrave_complaint($cmp_id=0,$user_id=0){	$sql="select cmp_id,cmp_title,cmp_expressnumber,cmp_orderno,cmp_deliverynumber,cmp_remark,".			"cmp_userid,cmp_parentid,cmp_time from ".$GLOBALS['engrave']->table('complaint').			" where cmp_isdelete = 0 and cmp_id =".$cmp_id." and cmp_userid=".$user_id;	$complaint = $GLOBALS['engrave_db']->getRow($sql);	$complaint['cmp_time'] = local_date('Y-m-d H:i:s',$complaint['cmp_time']);	return $complaint;}?>