<?php /** * ENGRAVE 商品管理、物流转运 程序 * ============================================================================ * * 版权所有  zxp，并保留所有权利。 * 网站地址:  * ============================================================================ * $Author: zxp $ * $Id: engrave_article_manage.php 2014-11-25 21:34:00 zxp $ */define('IN_ECS', true);require(dirname(__FILE__) . '/includes/init.php');/* 载入语言文件 */require_once(ROOT_PATH . 'languages/' .$_CFG['lang']. '/admin/engrave_article_manage.php');/*载入-内容管理-文章管理-数据访问文件*/require_once(ROOT_PATH . 'admin/includes/engrave/lib_article.php');/*上传*/require_once (ROOT_PATH  . 'admin/commonhelper/upload_json.php');require_once(ROOT_PATH . 'admin/includes/engrave/lib_channel_list.php');$channel_list= engrave_channel_list(0,0,'data',0,true);$smarty->assign('channel_list', $channel_list);/*------------------------------------------------------ *///-- 文章列表显示页面/*------------------------------------------------------ */if ($_REQUEST['act'] == 'list'){    /* 检查权限 */    admin_priv('article_manage');	    $ur_here = $_LANG['03_content_manage'] .'｜'.$_LANG['0301_article_manage'];    $smarty->assign('ur_here', $ur_here);    $ur_tip = empty($_LANG['engrave_article_manage_tip'])?'':$_LANG['engrave_article_manage_tip'];    $smarty->assign('ur_tip', $ur_tip);    $smarty->assign('action_link',  array('text' => $_LANG['0302_add_article'], 'href'=>'engrave_article_manage.php?act=add'));    $smarty->assign('full_page',    1);    //获取 频道 列表数据    /*载入-频道列表-数据访问文件*///    print_r($channel_list);//    print_a($channel_list);    $article_list=engrave_article_list();    $smarty->assign('article_list',   $article_list['article_list']);    $smarty->assign('filter',       $article_list['filter']);    $smarty->assign('record_count', $article_list['record_count']);    $smarty->assign('page_count',   $article_list['page_count']);       /* 显示商品列表页面 */    assign_query_info();        $smarty->display('engrave_article_manage.htm');}/*------------------------------------------------------ *///-- 文章列表显示页面-分页/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'query'){    $article_list=engrave_article_list();    $smarty->assign('article_list',   $article_list['article_list']);    $smarty->assign('filter',       $article_list['filter']);    $smarty->assign('record_count', $article_list['record_count']);    $smarty->assign('page_count',   $article_list['page_count']);	    $sort_flag  = sort_flag($article_list['filter']);    $smarty->assign($sort_flag['tag'], $sort_flag['img']);    	make_json_result($smarty->fetch('engrave_article_manage.htm'), '',			array('filter' => $article_list['filter'], 'page_count' => $article_list['page_count']));	}/*------------------------------------------------------ *///-- 文章添加-显示页面/*------------------------------------------------------ */elseif($_REQUEST['act'] == 'add'){    /* 检查权限 */    admin_priv('article_manage');    	/*载入-频道列表-数据访问文件*/	require_once(ROOT_PATH . 'admin/includes/engrave/lib_channel_list.php');	//获取 频道 列表数据	$channel_list= engrave_channel_list(0,0,true,0,true);	$smarty->assign('channel_list', $channel_list);	    /* 检查权限 */    admin_priv('0302_add_article');        $ur_here = $_LANG['03_content_manage'] .'｜'.$_LANG['0302_add_article'];    $smarty->assign('ur_here', $ur_here);    $smarty->assign('action_link',  array('text' => $_LANG['0301_article_manage'], 'href'=>'engrave_article_manage.php?act=list'));	$smarty->assign('form_act','insert');    $smarty->assign('full_page',    1);    $smarty->assign('CntTime', date('Y-m-d',time()));        $article_info=array("CntAuditingStatus" =>2);        $smarty->assign('article_info',$article_info);    /* 显示商品列表页面 */    assign_query_info();    $smarty->display('engrave_article_manage_info.htm');}/*------------------------------------------------------ *///-- 文章编辑-页面显示/*------------------------------------------------------ */elseif($_REQUEST['act']=='edit'){	/* 检查权限 */	admin_priv('article_manage');		/*载入-频道列表-数据访问文件*/	require_once(ROOT_PATH . 'admin/includes/engrave/lib_channel_list.php');    //获取ID，并根据ID获取对象 赋值给smarty    $CntId=$_REQUEST['CntId'];    $article_info=engrave_article($CntId);	$cntCategoryId=isset($article_info['CntCategoryId'])?intval($article_info['CntCategoryId']):'0';	$channel_list= engrave_channel_list(0,$cntCategoryId,true,0,true);	$smarty->assign('channel_list', $channel_list);		$smarty->assign('article_info', $article_info);	$ur_here = $_LANG['03_content_manage'] .'｜'.$_LANG['0302_add_article'];	$smarty->assign('ur_here', $ur_here);	$smarty->assign('action_link',  array('text' => $_LANG['0301_article_manage'], 'href'=>'engrave_article_manage.php?act=list'));	$smarty->assign('form_act','update');	$smarty->assign('full_page',    1);		/* 显示商品列表页面 */	assign_query_info();	$smarty->display('engrave_article_manage_info.htm');}/*------------------------------------------------------ *///-- 文章添加-数据操作/*------------------------------------------------------ */elseif($_REQUEST['act']=='insert' || $_REQUEST['act']=='update'){	$act=$_REQUEST['act'];    /* 检查权限 */    admin_priv('article_manage');    $upload=new FileUpload();    foreach ($_FILES AS $code => $file)    {     	$filename = $upload-> upload($file);	    if($filename!='error')	    {	    	$article['CntTitleImage']=$filename;	    }	    else {	    	sys_msg(sprintf($_LANG['msg_upload_failed'], $file['name'], $file_var_list[$code]['store_dir']));	    }    }	//获取数据	$article['CntCategoryId'] = !empty($_POST['CntCategoryId']) ? intval($_POST['CntCategoryId']) : '0';	$article['CntTitle'] = !empty($_POST['CntTitle']) ? trim($_POST['CntTitle']) : '';	$article['CntSubhead'] = !empty($_POST['CntSubhead']) ? trim($_POST['CntSubhead']) : '';	$article['CntAttributes'] = !empty($_POST['CntAttributes']) ? intval($_POST['CntAttributes']) : '0';	$article['oldCntTitleImage'] = !empty($_POST['oldCntTitleImage']) ? trim($_POST['oldCntTitleImage']) : '';	$article['CntMetaKey'] = !empty($_POST['CntMetaKey']) ? trim($_POST['CntMetaKey']) : '';	$article['CntMetaDes'] = !empty($_POST['CntMetaDes']) ? trim($_POST['CntMetaDes']) : '';	$article['CntExtLink'] = !empty($_POST['CntExtLink']) ? trim($_POST['CntExtLink']) : '';	$article['CntContent'] = !empty($_POST['CntContent']) ? trim($_POST['CntContent']) : '';	$article['CntBriefIntroduction'] = !empty($_POST['CntBriefIntroduction']) ? trim($_POST['CntBriefIntroduction']) : '';		$article['CntTag'] = !empty($_POST['CntTag']) ? trim($_POST['CntTag']) : '';		$article['CntAuthor'] = !empty($_POST['CntAuthor']) ? trim($_POST['CntAuthor']) : '';	$article['CntAuthorEmail'] = !empty($_POST['CntAuthorEmail']) ? trim($_POST['CntAuthorEmail']) : '';	$article['CntSourceWeb'] = !empty($_POST['CntSourceWeb']) ? trim($_POST['CntSourceWeb']) : '';	$article['CntSourceUrl'] = !empty($_POST['CntSourceUrl']) ? trim($_POST['CntSourceUrl']) : '';		$article['CntTime'] = !empty($_POST['CntTime']) ? $_POST['CntTime'] : date('Y-m-d H:i:s',time());	$article['CntAuditingStatus'] = !empty($_POST['CntAuditingStatus']) ? intval($_POST['CntAuditingStatus']) : '0';		if($act=='insert')	{		engrave_article_insert($article);		//页面跳转		$link[0]['text'] = $_LANG['continue_add_article'];		$link[0]['href'] = 'engrave_article_manage.php?act=add';		$link[1]['text'] = $_LANG['back_article_list'];		$link[1]['href'] = 'engrave_article_manage.php?act=list';	}	elseif($act='update')	{		$CntId=$_REQUEST['CntId'];		if($article['CntTitleImage']=='')		{			$article['CntTitleImage']=$article['oldCntTitleImage'];		}		engrave_article_update($article,$CntId);		$link[0]['text'] = $_LANG['back_article_list'];		$link[0]['href'] = 'engrave_article_manage.php?act=list';	}	sys_msg($_LANG['save_success'], 0, $link);}/*------------------------------------------------------ *///-- 文章删除：数据操作/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'remove'){    $cntId = intval($_REQUEST['id']);    /* 检查权限 */    check_authz_json('article_remove');    //根据ID获取文章名称    $article=engrave_article($cntId);    $articleName=$article['CntTitle'];        if (engrave_article_delete("1", $cntId))    {		// 记录日志		admin_log(addslashes($articleName), 'remove', 'site_manage');         $url = 'engrave_article_manage.php?act=query&' . str_replace('act=remove', '', $_SERVER['QUERY_STRING']);        ecs_header("Location: $url\n");        exit;    }}?>