<?php /** * ENGRAVE 业务逻辑：投诉理赔 * $Author: zhangxingpeng $ * $Id: lib_cmp.php 17217 2014年12月07日 11时03分00秒 zhangxingpeng $ */define('IN_ECS', true);require(dirname(__FILE__) . '/includes/init.php');/* 载入语言文件 */require_once(ROOT_PATH . 'languages/' .$_CFG['lang']. '/admin/engrave_complaint.php');/*载入-频道列表-数据访问文件*/require_once(ROOT_PATH . 'admin/includes/engrave/lib_complaint.php');/*------------------------------------------------------ *///-- 投诉理赔管理：投诉理赔列表-页面展示/*------------------------------------------------------ */if ($_REQUEST['act'] == 'list'){	/* 检查权限 */	admin_priv('complaint_manage');	$ur_here = $_LANG['10_consul_manage'] .'｜'.$_LANG['1002_complaint'];	$smarty->assign('ur_here', $ur_here);	$smarty->assign('full_page',    1);	//获取 投诉理赔 列表数据	$cmp_list = engrave_complaint_list();	$smarty->assign('cmp_list', $cmp_list['cmp_list']);	$smarty->assign('filter',        $cmp_list['filter']);	$smarty->assign('record_count',  $cmp_list['record_count']);	$smarty->assign('page_count',    $cmp_list['page_count']);	/* 显示购物指南页面 */	assign_query_info();	$smarty->display('engrave_complaint_list.htm');}/*------------------------------------------------------ *///-- 文章列表显示页面-分页、删除后显示/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'query'){	//获取 投诉理赔 列表数据	$cmp_list = engrave_complaint_list();	$smarty->assign('cmp_list', $cmp_list['cmp_list']);	$smarty->assign('filter',        $cmp_list['filter']);	$smarty->assign('record_count',  $cmp_list['record_count']);	$smarty->assign('page_count',    $cmp_list['page_count']);		$sort_flag  = sort_flag($cmp_list['filter']);	$smarty->assign($sort_flag['tag'], $sort_flag['img']);		make_json_result($smarty->fetch('engrave_complaint_list.htm'), '',	array('filter' => $cmp_list['filter'], 'page_count' => $cmp_list['page_count']));}/*------------------------------------------------------ *///-- 投诉理赔管理：投诉理赔回复-页面展示/*------------------------------------------------------ */elseif($_REQUEST['act'] == 'reply'){	/* 检查权限 */	admin_priv('complaint_manage');	$smarty->assign('action_link',  array('text' => $_LANG['1002_list_complaint'], 'href'=>'engrave_complaint.php?act=list'));		$ur_here = $_LANG['1002_complaint'] .'｜'.$_LANG['1002_reply_complaint'];	$smarty->assign('ur_here', $ur_here);	$smarty->assign('form_action', 'reply_insert');	//获取ID	$cmp_id = isset($_REQUEST['cmp_id']) ? intval($_REQUEST['cmp_id']) : 0;	$smarty->assign('cmp_id', $cmp_id);    $cmp = engrave_complaint($cmp_id);    $smarty->assign('cmp',$cmp);    $cmp_list = engrave_complaint_plist($cmp_id);    $smarty->assign('cmp_list', $cmp_list);	/* 显示投诉理赔页面 */	assign_query_info();	$smarty->display('engrave_complaint_info.htm');}/*------------------------------------------------------ *///-- 投诉理赔管理：投诉理赔查看-数据操作/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'view'){	/* 检查权限 */	admin_priv('complaint_manage');	//导航	$ur_here = $_LANG['1002_complaint'] .'｜'.$_LANG['1002_view_complaint'];	$smarty->assign('ur_here',$ur_here);	$smarty->assign('action_link',  array('text' => $_LANG['1002_list_complaint'], 'href'=>'engrave_complaint.php?act=list'));	//ID	$cmp_id = isset($_REQUEST['cmp_id']) ? intval($_REQUEST['cmp_id']) : 0;	//投诉理赔列表	$cmp = engrave_complaint($cmp_id);	$smarty->assign('cmp',$cmp);	$cmp_list = engrave_complaint_plist($cmp_id);	$smarty->assign('cmp_list', $cmp_list);	$smarty->assign('form_action', 'view');	$smarty->display('engrave_complaint_view.htm');}/*------------------------------------------------------ *///-- 投诉理赔管理：投诉理赔添加-数据操作/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'reply_insert'){    /* 检查权限 */    admin_priv('article_manage');	//添加	$operate = isset($_REQUEST['act']) ? trim($_REQUEST['act']) : '';	//获取插入数据/标题、包裹单号、订单号、运单号、备注//	$cmp['cmp_title'] = isset($_REQUEST['cmp_title']) ? trim($_REQUEST['cmp_title']) : '';//	$cmp['cmp_expressnumber'] = isset($_REQUEST['cmp_expressnumber']) ? trim($_REQUEST['cmp_expressnumber']) : '';//	$cmp['cmp_orderno'] = isset($_REQUEST['cmp_orderno']) ? trim($_REQUEST['cmp_orderno']) : '';//	$cmp['cmp_deliverynumber'] = isset($_REQUEST['cmp_deliverynumber']) ? trim($_REQUEST['cmp_deliverynumber']) : '';	$cmp['cmp_remark'] = isset($_REQUEST['cmp_remark']) ? trim($_REQUEST['cmp_remark']) : '';	//用户ID	$cmp['cmp_userid'] = isset($_SESSION['admin_id']) ? intval($_SESSION['admin_id']) : 0;	$cmp['cmp_parentid'] = isset($_REQUEST['cmp_id']) ? trim($_REQUEST['cmp_id']) : 0;		if(engrave_complaint_insert($cmp))	{		//添加成功		$link[0]['text'] = $_LANG['back_complaint_list'];		$link[0]['href'] = 'engrave_complaint.php?act=list';		sys_msg($_LANG['insert_complaint_success'], 0, $link);	}	else{		//添加失败		$link[0]['text'] = $_LANG['back_complaint_list'];		$link[0]['href'] = 'engrave_complaint.php?act=list';		sys_msg($_LANG['insert_complaint_failed'], 0, $link);	}}/*------------------------------------------------------ *///-- 投诉理赔删除：数据操作/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'remove'){	$cmp_id = intval($_REQUEST['id']);	/* 检查权限 */	check_authz_json('complaint_remove');	if (engrave_complaint_delete($cmp_id))	{		$url = 'engrave_complaint.php?act=query&' . str_replace('act=remove', '', $_SERVER['QUERY_STRING']);		ecs_header("Location: $url\n");		exit;	}}?>