<?php /** * ENGRAVE 业务逻辑：有问必答 * =========================================================== * * 版权所有 2014-2019 青岛友腾信息科技有限公司，并保留所有权利。 * 网站地址: http://www.qdutsoft.com； * ---------------------------------------------------------- * 这不是一个自由软件！您只能在不用于商业目的的前提下对程序代码进行修改和 * 使用；不允许对程序代码以任何形式任何目的的再发布。 * ========================================================== * $Author: zhangxingpeng $ * $Id: lib_faq.php 17217 2014年12月07日 11时03分00秒 zhangxingpeng $ */define('IN_ECS', true);require(dirname(__FILE__) . '/includes/init.php');/* 载入语言文件 */require_once(ROOT_PATH . 'languages/' .$_CFG['lang']. '/admin/engrave_faq.php');/*载入-频道列表-数据访问文件*/require_once(ROOT_PATH . 'admin/includes/engrave/lib_faq.php');/*------------------------------------------------------ *///-- 有问必答管理：有问必答列表-页面展示/*------------------------------------------------------ */if ($_REQUEST['act'] == 'list'){	/* 检查权限 */	admin_priv('faq_manage');	$ur_here = $_LANG['10_consul_manage'] .'｜'.$_LANG['1001_faq'];	$smarty->assign('ur_here', $ur_here);	$smarty->assign('full_page',    1);	//获取 有问必答 列表数据	$faq_list = engrave_faq_list();	$smarty->assign('faq_list', $faq_list['faq_list']);	$smarty->assign('filter',        $faq_list['filter']);	$smarty->assign('record_count',  $faq_list['record_count']);	$smarty->assign('page_count',    $faq_list['page_count']);	/* 显示购物指南页面 */	assign_query_info();	$smarty->display('engrave_faq_list.htm');}/*------------------------------------------------------ *///-- 文章列表显示页面-分页、删除后显示/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'query'){	//获取 有问必答 列表数据	$faq_list = engrave_faq_list();	$smarty->assign('faq_list', $faq_list['faq_list']);	$smarty->assign('filter',        $faq_list['filter']);	$smarty->assign('record_count',  $faq_list['record_count']);	$smarty->assign('page_count',    $faq_list['page_count']);		$sort_flag  = sort_flag($faq_list['filter']);	$smarty->assign($sort_flag['tag'], $sort_flag['img']);		make_json_result($smarty->fetch('engrave_faq_list.htm'), '',	array('filter' => $faq_list['filter'], 'page_count' => $faq_list['page_count']));}/*------------------------------------------------------ *///-- 有问必答管理：有问必答回复-页面展示/*------------------------------------------------------ */elseif($_REQUEST['act'] == 'reply'){	/* 检查权限 */	admin_priv('faq_manage');	$smarty->assign('action_link',  array('text' => $_LANG['1001_list_faq'], 'href'=>'engrave_faq.php?act=list'));		$ur_here = $_LANG['1001_faq'] .'｜'.$_LANG['1001_reply_faq'];	$smarty->assign('ur_here', $ur_here);	$smarty->assign('form_action', 'reply_insert');	//获取ID	$faq_id = isset($_REQUEST['faq_id']) ? intval($_REQUEST['faq_id']) : 0;	$smarty->assign('faq_id', $faq_id);    $faq_list = engrave_faq_list($faq_id);    $pfaq_list = engrave_faq_plist($faq_id);//    print_a($faq_list['faq_list'][0]);    $smarty->assign('faq', $faq_list['faq_list'][0]);    $smarty->assign('pfaq', $pfaq_list);	/* 显示有问必答页面 */	assign_query_info();	$smarty->display('engrave_faq_info.htm');}/*------------------------------------------------------ *///-- 有问必答管理：有问必答查看-数据操作/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'view'){	/* 检查权限 */	admin_priv('faq_manage');	//导航	$ur_here = $_LANG['1001_faq'] .'｜'.$_LANG['1001_view_faq'];	$smarty->assign('ur_here',$ur_here);	$smarty->assign('action_link',  array('text' => $_LANG['1001_list_faq'], 'href'=>'engrave_faq.php?act=list'));	//ID	$faq_id = isset($_REQUEST['faq_id']) ? intval($_REQUEST['faq_id']) : 0;	//有问必答列表//	$faq = engrave_faq($faq_id);//	$smarty->assign('faq',$faq);//	$faq_list = engrave_faq_plist($faq_id);    $faq_list = engrave_faq_list($faq_id);    $pfaq_list = engrave_faq_plist($faq_id);    $smarty->assign('faq', $faq_list['faq_list'][0]);    $smarty->assign('pfaq', $pfaq_list);//	$smarty->assign('faq_list', $faq_list);	$smarty->assign('form_action', 'view');	$smarty->display('engrave_faq_info.htm');}/*------------------------------------------------------ *///-- 有问必答管理：有问必答添加-数据操作/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'reply_insert'){    /* 检查权限 */    admin_priv('article_manage');	//添加	$operate = isset($_REQUEST['act']) ? trim($_REQUEST['act']) : '';	//获取插入数据/标题、包裹单号、订单号、运单号、备注//	$faq['faq_title'] = isset($_REQUEST['faq_title']) ? trim($_REQUEST['faq_title']) : '';//	$faq['faq_expressnumber'] = isset($_REQUEST['faq_expressnumber']) ? trim($_REQUEST['faq_expressnumber']) : '';//	$faq['faq_orderno'] = isset($_REQUEST['faq_orderno']) ? trim($_REQUEST['faq_orderno']) : '';//	$faq['faq_deliverynumber'] = isset($_REQUEST['faq_deliverynumber']) ? trim($_REQUEST['faq_deliverynumber']) : '';	$faq['faq_remark'] = isset($_REQUEST['faq_remark']) ? trim($_REQUEST['faq_remark']) : '';	//用户ID	$faq['faq_userid'] = isset($_SESSION['admin_id']) ? intval($_SESSION['admin_id']) : 0;	$faq['faq_parentid'] = isset($_REQUEST['faq_id']) ? trim($_REQUEST['faq_id']) : 0;		if(engrave_pfaq_insert($faq['faq_parentid'], $faq))	{		//添加成功		$link[0]['text'] = $_LANG['back_faq_list'];		$link[0]['href'] = 'engrave_faq.php?act=list';		sys_msg($_LANG['insert_faq_success'], 0, $link);	}	else{		//添加失败		$link[0]['text'] = $_LANG['back_faq_list'];		$link[0]['href'] = 'engrave_faq.php?act=list';		sys_msg($_LANG['insert_faq_failed'], 0, $link);	}}/*------------------------------------------------------ *///-- 有问必答删除：数据操作/*------------------------------------------------------ */elseif ($_REQUEST['act'] == 'remove'){	$faq_id = intval($_REQUEST['id']);	/* 检查权限 */	check_authz_json('faq_remove');	if (engrave_faq_delete($faq_id))	{		$url = 'engrave_faq.php?act=query&' . str_replace('act=remove', '', $_SERVER['QUERY_STRING']);		ecs_header("Location: $url\n");		exit;	}}?>